import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {
    cartIsShown: false,
    items: [],
};

const cartSlice = createSlice({
    name: 'cartReducer',
    initialState: initialCartState,
    reducers: {
        cartToggle() {
            state.cartIsShown = !state.cartIsShown;
        },
        addItem(state, action) {
            const existingItemIndex = state.items.findIndex(item => item.title === action.payload.title);
            let updatedItem;
            if (existingItemIndex) {
                const existingItem = state.items[existingItemIndex];
                updatedItem = {...existingItem, amount: existingItemIndex.amount + action.payload.amount}
                state = [...state.items, updatedItem];
            } else {
                state.items.push(action.payload);
            }
        },
        removeItem() {
            const existingItemIndex = state.items.findIndex(item => item.title === action.payload.title);
            const existingItem = state.items[existingItemIndex];
            if (existingItem.amount === 1) {
                state.items = state.items.filter(item => item.title !== payload.title);

            } else {
                state.items[existingItemIndex].amount--;
            }
        },
    },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
