import styles from "./Notification.module.css";

const Notificaiton = (props) => {
  const { status, title, message } = props.notificaiton;

  let specialClass = "";

  if (status === "error") {
    specialClass = "error";
  }
  if (status === "success") {
    specialClass = "success";
  }

  const cssClasses = `${styles.notificaiton} ${specialClass}`;

  return (
    <section className={cssClasses}>
      <h2>{title}</h2>
      <p>{message}</p>
    </section>
  );
};

export default Notificaiton;
